<?php/*** Plugin Name: Woocommerce Catalog* Plugin URI: https://wordpress.org/plugins/* Description: Switch to Catalog mode with one click.* Version: 1.0* Author: Leonidas Maroulis* Author URI: http://www.maroulis.net.gr**/class Woocommerce_catalog_mode {    public $version = '20141106'; // Latest version release date Year-Month-Day	public $url = ''; // URL of plugin installation	public $path = ''; // Path of plugin installation	public $file = ''; // Path of this file    public $settings; // Settings object		//options	public $remove_add_to_cart_button;	public $add_custom_button;	public $custom_button_type;	public $remove_price;	public $load_more_button_text;	public $custom_button_link;		function __construct() {        $this->file = __file__;        $this->path = dirname($this->file) . '/';        $this->url = WP_PLUGIN_URL . '/' . plugin_basename(dirname(__file__)) . '/';				require_once ($this->path . 'include/php/settings.php');				$this->settings = new Woo_Catalog_Settings($this->file);				//assign options		$this->remove_add_to_cart_button=get_option('woo_Catalog_remove_add_to_cart_button');		$this->remove_price=get_option('woo_Catalog_remove_price');		$this->add_custom_button=get_option('woo_Catalog_add_custom_button');		$this->custom_button_type =get_option('woo_Catalog_custom_button_type');		$this->custom_button_link =get_option('woo_Catalog_custom_button_link');		$this->load_more_button_text		= get_option('woo_Catalog_button_text')==""?__( 'More', 'woocommerce_Catalog_mode' ):get_option('woo_Catalog_button_text');					if ($this->remove_add_to_cart_button=="on"){			add_action('init',array($this,'remove_add_to_cart_button'));		}		if ($this->remove_price=="on"){			add_action('init',array($this,'remove_price'));		}			add_action('wp_enqueue_scripts', array($this,'register_frontend_assets'));		add_action('wp_enqueue_scripts', array($this, 'load_frontend_assets'));						    }		public function register_frontend_assets() {        // Add frontend assets in footer		wp_register_style('woocommerce_Catalog_mode-frontend-custom-style', $this->url . 'include/css/style.php');		    }			public function load_frontend_assets() {		//load all scripts		wp_enqueue_style( 'woocommerce_Catalog_mode-frontend-custom-style' );    }		public function remove_add_to_cart_button(){		if ($this->add_custom_button=="on"){			add_filter('woocommerce_loop_add_to_cart_link', array($this,'custom_button'));		}else{			remove_action( 'woocommerce_after_shop_loop_item', 'woocommerce_template_loop_add_to_cart', 10 );				}				remove_action( 'woocommerce_single_product_summary', 'woocommerce_template_single_add_to_cart', 30 );			}		public function remove_price(){		remove_action( 'woocommerce_single_product_summary', 'woocommerce_template_single_price', 10 );		remove_action( 'woocommerce_after_shop_loop_item_title', 'woocommerce_template_loop_price', 10 );		}		public function custom_button(){			if ($this->custom_button_type=="custom_button_type_read_more"){			global $product;			echo ' <a id="woocommerce_Catalog_mode_custom_button" href="' . esc_url( $product->get_permalink( $product->id ) ) . '" class="single_add_to_cart_button button alt">'.$this->load_more_button_text.'</a>				  </a>';		}else{			echo ' <a id="woocommerce_Catalog_mode_custom_button" href="' . $this->custom_button_link . '" class="single_add_to_cart_button button alt">'.$this->load_more_button_text.'</a>				  </a>';		}		}				public function version() {        return $this->version;    }		}$l = new Woocommerce_catalog_mode(); ?>